const LoginForm = ((props)=>{
    const isLogIn = props.isLogIn
    const onSubmit = ((e)=>{
        const fData= new FormData(
            e.target
        )
        e.preventDefault(

        )
        const userName = fData.get("username")
        const password = fData.get("password")
        const confirmPassword = fData.get("confirmPassword")
            fetch (`https://strangers-things.herokuapp.com/api/2204-ftb-mt-web-pt/users/${isLogIn ? "login": "register" }`,
            {
                method: "POST",
                headers: {
                  'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                  user: {
                    username: userName,
                    password
                  }
                })
            }
            )
            .then(response => response.json())
            .then((data) => {
                const token = data.data.token
                console.log(data);
                window.localStorage.setItem("token", token)
            })
            .catch(console.error)
    })
    return <div>
        <form onSubmit={onSubmit}>
            <input type= "text" name= "username" required min={3}></input>
            <input type="password" name= "password" required min={6}></input>
            <input type="password" name ="confirmPassword" required></input>
            <button>Submit</button>
        </form>
    </div>
})

export default LoginForm;
