const PostForm = () => {
    const onSubmit = (e) => {
        e.preventDefault()
        const fData = new FormData(e.target)

        const params = {
            title: fData.get("title"),
            price: fData.get("price"),
            location: fData.get("location"),
            description: fData.get("description"),
            willDeliver: !!fData.get("willDeliver")

        }
        fetch ("")
    }
    return <div>
        <form onSubmit = {onSubmit}>
            <input type= "text" required min={1} name = "title" > </input>
            <input type= "text" required min={1} name = "price" > </input>
            <input type= "text" name = "location" > </input>
            <textarea name="description" required></textarea>
            <input type = "checkbox" name="willDeliver"></input>
            <button>Submit</button>
        </form>
    </div>;
}

export default PostForm
