import { useEffect, useState } from "react";
import { makeHeaders } from "./fetch";
import PostCard from "./PostCard";

const Post = (props) => {
  const [data, setData] = useState([])
    useEffect(() => {
        fetch("https://strangers-things.herokuapp.com/api/2204-FTB-MT-WEB-PT/posts", makeHeaders(
           "get",  
        ))
        .then(response => response.json())
        .then(response => setData(response.data.posts))
        .catch(console.error)
    },[])
    console.log(data);
    return <div>{
        data.map((post) => {
            return <PostCard {...post} posts = {props.posts} setData = {props.setData} />
        })
        }</div>
}
export default Post;
